  onAddItem : ()=>{},
  onRemoveItem: ()=>{},
  meals:[],
  total:0
})

export const CartContextProvider = (props)=>{
  const [cartItems, setCartItems] = useState([])
  const [total,setTotal] = useState(0)
  const [count,setCount] =useState(0)
 


  const onSetTotal = (price)=>{
      setTotal((prev)=>{
        const amount = price + prev
        return amount
      })
  }
