import React,{useState, useEffect} from 'react'

const CartContext = React.createContext({
  cartCount :0,
  onAddItem : ()=>{},
  onRemoveItem: ()=>{},
  meals:[],
  total:0
})

export const CartContextProvider = (props)=>{
  const [cartItems, setCartItems] = useState([])
  const [total,setTotal] = useState(0)
  const [count,setCount] =useState(0)
 
  console.log(cartItems)

  const onSetTotal = (price)=>{
      setTotal((prev)=>{
        const amount = price + prev
        console.log(amount.toFixed(2))
        return amount
      })
  }

  const onSetCartCount = (quantity)=>{
    setCount((prev)=>{
      return quantity + prev
    })
  }

  const addItem = (item)=>{
    
    setCartItems((prevState )=>{
      return [item,...prevState]
    })
  }

  return (
    <CartContext.Provider value={{onAddItem: addItem, count: count, meals:cartItems, total: total,onSetTotal:onSetTotal, onSetCartCount: onSetCartCount}}>
      {props.children}
    </CartContext.Provider>
  )
}

export default CartContext
